    scanf("%d %d", &n, &q);
    int **arr = (int **)malloc(n * sizeof(int*));
    int *size = (int*)calloc(n, sizeof(int));
    int *capacity = (int*)malloc(n * sizeof(int));
    for (int i = 0; i < n; i++) {
        capacity[i] = 4;
        arr[i] = (int*)malloc(capacity[i] * sizeof(int));
    }
    int lastAnswer = 0;
    int *answers = (int*)malloc(q * sizeof(int));
    int answerCount = 0;
    for (int i = 0; i < q; i++) {
        int type, x, y;
        scanf("%d %d %d", &type, &x, &y);
        int idx = (x ^ lastAnswer) % n;
        if (type == 1) {
            if (size[idx] == capacity[idx]) {
                capacity[idx] *= 2;
                arr[idx] = (int*)realloc(arr[idx], capacity[idx] * sizeof(int));
            }
            arr[idx][size[idx]] = y;
            size[idx]++;
        } else {
            int pos = y % size[idx];
            lastAnswer = arr[idx][pos];
            answers[answerCount++] = lastAnswer;
        }
    }
    for (int i = 0; i < answerCount; i++) {
        printf("%d\n", answers[i]);
    }
    for (int i = 0; i < n; i++) {
        free(arr[i]);
    }
    free(arr);
    free(size);
    free(capacity);
    free(answers);
    return 0;
}

