    int maxAnd = 0, maxOr = 0, maxXor = 0;

    for (int i = 1; i <= n; i++) {
        for (int j = i + 1; j <= n; j++) {
            int andRes = i & j;
            int orRes = i | j;
            int xorRes = i ^ j;
            if (andRes < k && andRes > maxAnd) {
                maxAnd = andRes;
            }
            if (orRes < k && orRes > maxOr) {
                maxOr = orRes;
            }
            if (xorRes < k && xorRes > maxXor) {
