#include <stdio.h>
#include <stdlib.h>
#include <string.h>

typedef struct {
    char *data;
    int size;
} EditorState;

EditorState *createState() {
    EditorState *state = malloc(sizeof(EditorState));
    state->data = malloc(1);
    state->data[0] = '\0';
    state->size = 0;
    return state;
}

void freeState(EditorState *state) {
    free(state->data);
    free(state);
}

void append(EditorState *state, const char *W) {
    int len = strlen(W);
    state->data = realloc(state->data, state->size + len + 1);
    strcpy(state->data + state->size, W);
    state->size += len;
}

void delete(EditorState *state, int k) {
    if (k > state->size) {
        k = state->size;
    }
    state->size -= k;
    state->data[state->size] = '\0';
}

char printChar(EditorState *state, int k) {
    return state->data[k - 1];
}

void undo(EditorState **history, int *top, EditorState *current) {
    if (*top >= 1) {
        EditorState *prevState = history[--(*top)];
        free(current->data);
        current->data = prevState->data;
        current->size = prevState->size;
        free(prevState);
    }
}

int main() {
    int Q;
    scanf("%d", &Q);
    EditorState *current = createState();
    EditorState **history = malloc(Q * sizeof(EditorState *));
    int top = 0;

    for (int i = 0; i < Q; i++) {
        int operationType;
        scanf("%d", &operationType);

        if (operationType == 1) { // append(W)
            char W[1000001]; // Assuming W max length is 1000000
            scanf("%s", W);
            history[top++] = createState();
            history[top - 1]->data = malloc(current->size + 1);
            strcpy(history[top - 1]->data, current->data);
            history[top - 1]->size = current->size;
            append(current, W);
        } else if (operationType == 2) { // delete(k)
            int k;
            scanf("%d", &k);
            history[top++] = createState();
            history[top - 1]->data = malloc(current->size + 1);
            strcpy(history[top - 1]->data, current->data);
            history[top - 1]->size = current->size;
            delete(current, k);
        } else if (operationType == 3) { // print(k)
            int k;
            scanf("%d", &k);
            printf("%c\n", printChar(current, k));
        } else if (operationType == 4) { // undo()
            undo(history, &top, current);
        }
    }

    freeState(current);
    for (int i = 0; i < top; i++) {
        freeState(history[i]);
    }
    free(history);

    return 0;
}

