long largestRectangle(int* h, int n) {
    long maxArea = 0;
    int *stack = (int *)malloc(n * sizeof(int));
    int top = -1;

    for (int i = 0; i <= n; i++) {
        int currentHeight = (i == n) ? 0 : h[i];
        while (top != -1 && h[stack[top]] > currentHeight) {
            int height = h[stack[top--]];
            int width = (top == -1) ? i : i - stack[top] - 1;
            long area = height * width;
            if (area > maxArea) {
                maxArea = area;
            }
        }
        stack[++top] = i;
    }

    free(stack);
    return maxArea;
}

int main() {
