#include <stdio.h>
#include <stdlib.h>

typedef struct Stack {
    long long *arr;
    int top;
    int capacity;
} Stack;

void initStack(Stack *s, int capacity) {
    s->arr = (long long *)malloc(sizeof(long long) * capacity);
    s->top = -1;
    s->capacity = capacity;
}

void push(Stack *s, long long val) {
    if (s->top + 1 == s->capacity) {
        s->capacity *= 2;
        s->arr = (long long *)realloc(s->arr, sizeof(long long) * s->capacity);
    }
    s->arr[++s->top] = val;
}

long long pop(Stack *s) {
    return s->arr[s->top--];
}

long long peek(Stack *s) {
    return s->arr[s->top];
}

int main() {
    int q;
    scanf("%d", &q);
    Stack enqStack, deqStack;
    initStack(&enqStack, 1024);
    initStack(&deqStack, 1024);

    for (int i = 0; i < q; i++) {
        int type;
        scanf("%d", &type);
        if (type == 1) {
            long long x;
            scanf("%lld", &x);
            push(&enqStack, x);
        } else if (type == 2) {
            if (deqStack.top == -1) {
                while (enqStack.top != -1) {
                    push(&deqStack, pop(&enqStack));
                }
            }
            pop(&deqStack);
        } else if (type == 3) {
            if (deqStack.top == -1) {
                while (enqStack.top != -1) {
                    push(&deqStack, pop(&enqStack));
                }
            }
            printf("%lld\n", peek(&deqStack));
        }
    }

    free(enqStack.arr);
    free(deqStack.arr);
    return 0;
}

