long long calculateSimilarity(char* s) {
    int n = strlen(s);
    long long totalSimilarity = 0;
    int z[n];
    z[0] = 0;

    for (int i = 1, l = 0, r = 0; i < n; i++) {
        if (i > r) {
            l = r = i;
            while (r < n && s[r] == s[r - l]) r++;
            z[i] = r - l;
            r--;
        } else {
            int k = i - l;
            if (z[k] < r - i + 1) {
                z[i] = z[k];
            } else {
                l = i;
                while (r < n && s[r] == s[r - l]) r++;
                z[i] = r - l;
                r--;
            }
        }
        totalSimilarity += z[i];
    }

    return totalSimilarity + n; // adding n for the string itself
}

int main() {
    int t;
    scanf("%d", &t);

    while (t--) {
        char s[100001];
        scanf("%s", s);
        printf("%lld\n", calculateSimilarity(s));
    }

    return 0;
}

