#include <stdio.h>
#include <stdint.h>

const int MOD = 100;

uint64_t mod_pow(uint64_t base, uint64_t exp, uint64_t mod) {
    uint64_t result = 1;
    base %= mod;
    while (exp > 0) {
        if (exp & 1) result = (result * base) % mod;
        base = (base * base) % mod;
        exp >>= 1;
    }
    return result;
}

uint64_t calculate_sum(uint64_t K, uint64_t N) {
    uint64_t sum = 0;
    uint64_t full_cycles = K / 100;
    uint64_t remainder = K % 100;

    for (uint64_t i = 1; i <= 100; i++) {
        sum = (sum + mod_pow(i, N, MOD)) % MOD;
    }
    sum = (sum * full_cycles) % MOD;

    for (uint64_t i = 1; i <= remainder; i++) {
        sum = (sum + mod_pow(i, N, MOD)) % MOD;
    }

    return sum;
}

int main() {
    int T;
    scanf("%d", &T);
    for (int t = 0; t < T; t++) {
        uint64_t K, N;
        scanf("%llu %llu", &K, &N);
        uint64_t result = calculate_sum(K, N);
        printf("%02llu\n", result);
    }
    return 0;
}

