#include <stdio.h>
#include <stdlib.h>

typedef struct {
    long long value;
    long long max;
} StackNode;

typedef struct {
    StackNode *data;
    int top;
    int capacity;
} Stack;

void initStack(Stack *s, int capacity) {
    s->data = (StackNode*) malloc(sizeof(StackNode) * capacity);
    s->top = -1;
    s->capacity = capacity;
}

void push(Stack *s, long long val) {
    if (s->top + 1 == s->capacity) {
        s->capacity *= 2;
        s->data = (StackNode*) realloc(s->data, sizeof(StackNode) * s->capacity);
    }
    long long maxVal = val;
    if (s->top >= 0 && s->data[s->top].max > maxVal) {
        maxVal = s->data[s->top].max;
    }
    s->top++;
    s->data[s->top].value = val;
    s->data[s->top].max = maxVal;
}

void pop(Stack *s) {
    if (s->top >= 0) {
        s->top--;
    }
}

long long getMax(Stack *s) {
    if (s->top >= 0)
        return s->data[s->top].max;
    return -1;
}

int main() {
    int n;
    scanf("%d", &n);
    Stack stack;
    initStack(&stack, 1024);
    for (int i = 0; i < n; i++) {
        int cmd;
        scanf("%d", &cmd);
        if (cmd == 1) {
            long long x;
            scanf("%lld", &x);
            push(&stack, x);
        } else if (cmd == 2) {
            pop(&stack);
        } else if (cmd == 3) {
            printf("%lld\n", getMax(&stack));
        }
    }
    free(stack.data);
    return 0;
}

