char* isBalanced(char* s) {
    int len = strlen(s);
    char* stack = (char*)malloc(len + 1);
    int top = -1;

    for (int i = 0; i < len; i++) {
        char ch = s[i];

        if (ch == '{' || ch == '(' || ch == '[') {
            stack[++top] = ch;
        } else {
            if (top == -1) {
                free(stack);
                return "NO";
            }
            char popped = stack[top--];
            if ((ch == '}' && popped != '{') || 
                (ch == ')' && popped != '(') || 
                (ch == ']' && popped != '[')) {
                free(stack);
                return "NO";
            }
        }
    }

    free(stack);
    return (top == -1) ? "YES" : "NO";
}

